// import from next.js link api for client-side navigation
import Link from 'next/link';
import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';

const Index = props => (
    <Layout>
        <h1>wthr-next</h1><span>ft. dark sky api</span>
        <ul>
            {props.conditions.map(condition => (
                <li key={condition.id}>
                    <Link href='/p/[id]' as={`/p/${condition.id}`}>
                        <a>{condition.name}</a>
                    </Link>
                </li>
            ))}
        </ul>
    </Layout>
);

Index.getInitialProps = async function() {
    const res = await fetch('https://api.darksky.net/forecast/0e17dfdfdee5ff8839c6ffead1cf1b74/37.8267,-122.4233');
    const data = await res.json();

    console.log(`Show data fetched. Condition count: ${data.currently.summary}`);

    return {
        conditions: data.map(entry => entry.condition)
    };
};

export default Index;
