// import from next.js link api for client-side navigation
import Link from 'next/link';
import Layout from '../components/Layout';

const PostLink = props => (
    <li>
        <Link href="/p/[id]" as={`/p/${props.id}`}>
            <a>{props.id}</a>
        </Link>
    </li>
);

const Index = () => (
    <div>
        <Layout>
            <h1>wthr-next</h1>
            <span>if you'd like to know more, read </span>
            <Link href='/about'>
                <a>about us</a>
            </Link>
            <ul>
                <PostLink id='allegra'></PostLink>
                <PostLink id='mary'></PostLink>
                <PostLink id='kyichu'></PostLink>
                <PostLink id='soba'></PostLink>
                <PostLink id='theda'></PostLink>
            </ul>
        </Layout>
    </div>
);

export default Index;
