import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

const Index = props => (
  <Layout>
    <h1>Batman TV Shows</h1>
    <ul>
      {props.conditions.map(condition => (
        <li key={condition.id}>
          <Link href="/p/[id]" as={`/p/${condition.latitude}/${condition.longitude}`}>
            <a>{show.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch('https://api.darksky.net/forecast/0e17dfdfdee5ff8839c6ffead1cf1b74/37.8267,-122.4233');
  const data = await res.json();

  console.log(`D A T A : ${data}`);
  console.log(`S T R I N G I F I E D : ${JSON.stringify(data)}`);
  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    shows: data.map(entry => entry.show)
  };
};

export default Index;
